import React, { Component } from 'react';

import HeaderAll from '../../Header/HeaderAll';
import HotelInfoTags from './HotelInfoTags';
import FooterAll from '../../Footer/FooterAll';
import { DataHotelInfo } from './DataHotelInfo';
import HotelInfo from './HotelInfo';
import './Hotel.css';
import SearchBox from './Searchbox';




class HotelInfoMain extends Component {
    constructor() {
        super()
        this.state = {
            SearchH: '',
            SearchS: '',
            Searchfield: '',
            DataHotelInfo: DataHotelInfo

        }
    }


    onSearchChange = (event) => {

        if (event.target.name === "Hotelname") {
            this.setState({ SearchH: event.target.value })
            console.log(this.state)
        }
        else if (event.target.name === "HotelStart") {
            this.setState({ SearchS: event.target.value })
            console.log(this.state)
        }
        else if (event.target.name === "Description") {
            this.setState({ Description: event.target.value })
            console.log(this.state)
        }



    }

    render() {
        {/*
         const filteredHotels = this.state.DataHotelInfo.filter(DataHotelInfo => {
            return DataHotelInfo.HotelName.toLowerCase().includes(this.state.SearchH.toLowerCase())
        });
       */}


        const filteredHotels = this.state.DataHotelInfo.filter(DataHotelInfo =>
            DataHotelInfo.HotelName.toLowerCase().includes(this.state.SearchH.toLowerCase()) && DataHotelInfo.Stars.toLowerCase().includes(this.state.SearchS.toLowerCase())).map((result) => {
                return (
                    <HotelInfo result={result} />

                )
            });

        {/*
        const Hotelinfo = filteredHotels.map((result) => {
            return (
                <HotelInfo result={result} />
            )
        });
        */}

        return (

            <div>
                <div className="outer-container">
                    <HotelInfoTags />
                    <HeaderAll titlePage="Hotels" />

                    <div className="HotelInfo">

                        <div className="HotelInfo-container">
                            <SearchBox searchchange={this.onSearchChange} />
                            {filteredHotels}


                        </div>

                    </div>


                </div>
                <FooterAll />
            </div>


        );
    }
}



export default HotelInfoMain;
